import incomeTransactions from './GlobalState'
export default (state, action) => {
    switch (action.type) {
        case 'ADD-INCOME':
            return{
                ...state,
                incomeTransactions: [action.payload,...incomeTransactions]

            }
        default:
            return state;
    }
}
