import React, { createContext, useReducer } from 'react'
import AppReducer from './AppReducer'
import IncomeTransaction from '../component/IncomeTransaction';

const initialState = {
    incomeTransactions: [
        { id: 1, incomeText: "Car Sold", incomeAmount: 15000 },
        { id: 2, incomeText: "Salary", incomeAmount: 15000 },
        { id: 3, incomeText: "Bonus", incomeAmount: 1000 }

    ],
    expenseTransactions: [
        { id: 4, expenseText: "Rent", expenseAmount: 500 },
        { id: 5, expenseText: "Book", expenseAmount: 50 },
        { id: 6, expenseText: "Clothes", expenseAmount: 100 }

    ]
};

export const GlobalContext = createContext(initialState);

export const GlobalContextProvider = ({ children }) => {
    const [state, dispatch] = useReducer(AppReducer, initialState)
    const addIncome = IncomeTransaction => {
        dispatch(
            {type: 'ADD-INCOME',
            payload: IncomeTransaction},
         ) 

    }

    return (
        <GlobalContext.Provider value={{
            incomeTransactions: state.incomeTransactions,
            expenseTransactions: state.expenseTransactions,
            addIncome,
        }}>
            {children}

        </GlobalContext.Provider>
    )
};
