import React, { useState } from 'react';


const AddTransaction = () => {
    const [income,setIncome] = useState({
        incomeText: "",
        incomeAmount: 0,
    })
    const {incomeText,incomeAmount} = income
    const onChangeIncome = e => {
        setIncome({...income,[e.target.name]: e.target.value});

        console.log(income)

    }
    const onSubmitIncome = (e)=> {
        e.preventDefault();
        const newIncomeTransaction = {
            id: Math.floor(Math.random() * 10000),
            incomeText,
            incomeAmount
        }
    }
    return (
        <div className="form-wrapper">
            <form onSubmit={onSubmitIncome}>
                <div className="input-group income">
                    <input name="incomeText" type="text" placeholder="Add income..." autoComplete="off" onChange={onChangeIncome}/><br/>
                    <input name="incomeAmount" type="number" placeholder="Amount" autoComplete="off" onChange={onChangeIncome}/><br/>
                    <input type="submit" value="Submit" />
                </div>

            </form>
            <form>
                <div className="input-group expense">
                    <input type="text" placeholder="Add Expense..." autoComplete="off"/><br/>
                    <input type="number" placeholder="Amount" autoComplete="off" /><br/>
                    <input type="submit" value="Submit" />
                </div>

            </form>
        </div>

    )
}
export default AddTransaction;
